class Collection extends Array {
	constructor(...args) {
		super(...args);
	}

	add(obj) {
		this.push(obj);
	}

	remove(obj, isIndex = true) {
		if(isIndex) {
			this.splice(obj, 1);
		} else {
			let i = this.indexOf(obj);
			if(i >= 0) {
				this.splice(i, 1);
			} else {
				throw new Error('Cannot remove item from Collection with index less than 0');
			}
		}

		return this;
	}

	size() {
		return this.length;
	}
}

module.exports = Collection;