String.prototype.toUnderscoreCase = function() {
	return this.replace(/[A-Z]+/g, function (x,y){return x.toLowerCase()});
}

String.prototype.toCamelCase = function() {
	let parts = this.split('-');
	let output = '';
	parts.forEach((value, index) => {
		if(index === 0) {
			output += value;
		} else {
			output += value.substring(0, 1).toUpperCase() + value.slice(1);
		}
	});
	return output;
}
